-- vibes: auxiliary reference table
create table "public"."vibes" (
  "id" bigint generated by default as identity not null,
  "created_at" timestamp with time zone not null default now(),
  "name" text not null,
  "user_id" uuid not null default auth.uid()
);

alter table "public"."vibes" enable row level security;

-- dances_vibes: many-to-many junction
create table "public"."dances_vibes" (
  "id" bigint generated by default as identity not null,
  "created_at" timestamp with time zone not null default now(),
  "dance_id" bigint not null,
  "vibe_id" bigint not null
);

alter table "public"."dances_vibes" enable row level security;

-- Primary keys
CREATE UNIQUE INDEX vibes_pkey ON public.vibes USING btree (id);
CREATE UNIQUE INDEX dances_vibes_pkey ON public.dances_vibes USING btree (id);

-- Unique name per user
CREATE UNIQUE INDEX vibes_name_user_key ON public.vibes USING btree (name, user_id);

-- FK indexes
CREATE INDEX ON public.vibes (user_id);
CREATE INDEX ON public.dances_vibes (dance_id);
CREATE INDEX ON public.dances_vibes (vibe_id);

-- Constraints
alter table "public"."vibes" add constraint "vibes_pkey" PRIMARY KEY using index "vibes_pkey";
alter table "public"."dances_vibes" add constraint "dances_vibes_pkey" PRIMARY KEY using index "dances_vibes_pkey";

alter table "public"."vibes" add constraint "vibes_name_user_key" UNIQUE using index "vibes_name_user_key";

alter table "public"."vibes" add constraint "vibes_user_id_fkey" FOREIGN KEY (user_id) REFERENCES auth.users(id) ON DELETE CASCADE not valid;
alter table "public"."vibes" validate constraint "vibes_user_id_fkey";

alter table "public"."dances_vibes" add constraint "dances_vibes_dance_id_fkey" FOREIGN KEY (dance_id) REFERENCES public.dances(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;
alter table "public"."dances_vibes" validate constraint "dances_vibes_dance_id_fkey";

alter table "public"."dances_vibes" add constraint "dances_vibes_vibe_id_fkey" FOREIGN KEY (vibe_id) REFERENCES public.vibes(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;
alter table "public"."dances_vibes" validate constraint "dances_vibes_vibe_id_fkey";

