-- RLS policies
create policy "user_access"
on "public"."vibes"
as permissive
for all
using (user_id = (select auth.uid()))
with check (user_id = (select auth.uid()));

create policy "user_access"
on "public"."dances_vibes"
as permissive
for all
using (exists (
  select 1 from dances
  where dances.id = dance_id
  and dances.user_id = (select auth.uid())
));
